SELECT t.*,h.hospital_name,c.create_time,c.health_time,c.medical FROM tb_tijianinfo AS t,tb_hospital as h,tb_hearth_card as c
WHERE 1=1 AND t.idcard_num=c.id_card AND c.hospital_id=h.hospital_id

  and t.number=#{number}


  and t.tj_id=#{tjid}


  and t.tjtime between #{tjstime} and #{tjetime}


  and t.name=#{name}


  and t.sex=#{sex}


  and t.adress=#{adress}


  and t.company=#{company}


  and c.healthcard_num=#{healthnum}


  and c.medical=#{medical}


  and c.create_time between #{cstime} and #{cetime}

select

from tb_tijianinfo
order by create_time desc;

select

from tb_tijianinfo

  and date(create_time) >= #{startTime}


  and date(create_time) $lt;= #{endTime}

